Upload a file
const files = await os.showUploadFiles();

if (files.length <= 0) {
    return;
}

const file = files[0];
const result = await os.recordFile(tags.recordKey, file);

if (result.success) {
    tags.uploadUrl = result.url;
    os.toast("Success! Uploaded to " + result.url);
} else {
    os.toast("Failed " + result.errorMessage);
}

